/*
srv==0	//Broadcasting, analogue
srv==1	//Broadcasting, digital
srv==2	//Mobile, urban
srv==3	//Mobile, suburban, rolling hills
*/
/*
ENV=0 Open Area
ENV=1 Rural Area
ENV=2 SubUrban Area
ENV=3 Urban Area
ENV=4 Dense Urban Area
ENV=5 Cold Sea (North Sea)
ENV=6 Warm Sea (Mediterranean Sea)

p=1 -> land
p=2 -> cold sea
p=3 -> warm sea
*/

/*
	if		(ENV==0){	R=10;p=1;}
	else if (ENV==1){	R=10;p=1;}
	else if (ENV==2){	R=10;p=1;}
	else if (ENV==3){	R=20;p=1;}
	else if (ENV==4){	R=30;p=1;}
	else if (ENV==5){	R=10;p=2;}
	else if (ENV==6){	R=10;p=3;}
	else			{	R=10;p=1;}
*/
double CP1546_Functions::P1546_8(double f_MHz,double t,int p,double h1,double d_km,double h2,int ENV,double tetamax15,int srv,double L,double *Ebfs) 
{
	double E0fs, Eb, R = 10.0;

	if (p==1)
		R = 10.0*(ENV==0) + 10.0*(ENV==1) + 10.0*(ENV==2) + 20.0*(ENV==3) + 30.0*(ENV==4);

	Eb = P1546_7(f_MHz, t, p, h1, d_km, h2, R, tetamax15, &E0fs) ;
	*Ebfs = E0fs;

	if ((p==1)&&(L!=50))
	{
		double K,S,sigmaL1,EsigmaL;
		K = 5.1*(srv==0) + 5.5*(srv==1) + 2.1*(srv==2) + 3.8*(srv==3);
		S = 1.6*(srv==0) + 0.0*(srv==1) + 1.6*(srv==2) + 1.6*(srv==3);

		sigmaL1 = K + S * log10(f_MHz);
		EsigmaL = Qi(L) * sigmaL1;

		Eb = Eb +  EsigmaL;
		Eb = min(Eb,E0fs);
	}
	return Eb;
}

double CP1546_Functions::P1546_7(double f,double t,int p,double h1,double d_km,double h2,double R,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb,y,corrh2;

	Eb = P1546_6(f,t,p,h1,d_km,tetamax15,&E0fs);
	*Ebfs=E0fs;

	if (h2==R)
		corrh2 = 0.0;
	else
	{
		double Kh2=3.2+6.2*log10(f);

		if (p==1)   // Land
		{
			double Rp;
			h2=max(h2,1.0);
			if ( (6.5*d_km + R) >= h1 )
				Rp = R;
			else
				Rp = (1000.0*R*d_km - 15.0*h1)/(1000.0*d_km - 15.0);
			Rp=max(Rp,1.0);

			if ( (R==20.0) || (R==30.0) )
			{
				double Knu=0.0108*sqrt(f);
				if (Rp>h2)
				{
					double pi=4.0*atan(1.0);
					double hdif = Rp - h2;
					double tetaclut = (180.0/pi)*atan(hdif/15.0);
					double vv = (Knu*sqrt(hdif*tetaclut))-0.1;
					corrh2 = (6.03*h2/Rp)-(6.9+20.0*log10(vv + sqrt(1.0+vv*vv)));
				}
				else
					corrh2 = Kh2*log10(h2/Rp);
			}
			else if (R==10.0)   
				corrh2=Kh2*log10(h2/Rp);
		}
		else    // Sea
		{
			if (h2>=10.0)
				corrh2=Kh2*log10(h2/10.0);
			else if (h2<10.0)
			{
				h2=max(h2,3.0);
				double d10=zD06(f,h1,10.0);
				double dh2=zD06(f,h1,h2);
				if (d_km>=d10)
					corrh2 = Kh2*log10(h2/10.0);
				else if (d_km<=dh2)
					corrh2 = 0.0;
				else
				{
					double c10=Kh2*log10(h2/10.0);
					corrh2 = c10*log10(d_km/dh2)/log10(d10/dh2);
				}
			}
		}
	}
	y=Eb+corrh2;

	y=min(y,E0fs);
	return y;
}



double CP1546_Functions::P1546_6(double f,double t,int p,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;

	if ( (f==100.0) || (f==600.0) || (f==2000.0) ) 
		Eb = P1546_4(f,t,p,h1,d_km,tetamax15,&E0fs);
	else
	{
		if ( (f<100.0) && (p>1) )
		{
			double zzz1,Edf;
			h1= max(1.0 , h1);
			double df = zD06(f,h1,10.0);

			if (d_km<=df)
				zzz1 = P1546_5(f,t,p,h1,d_km,tetamax15,&Eb);
			else
			{
				zzz1 = P1546_5(f,t,p,h1,df,tetamax15,&Edf);
				double d600 = zD06(600.0,h1,10.0);
				double Ed600 = P1546_5(f,t,p,h1,d600,tetamax15,&zzz1);
				Eb = Edf + (Ed600 - Edf) * log10(d_km/df)/log10(d600/df);
			}
			zzz1 = P1546_5(f,t,p,h1,d_km,tetamax15,&E0fs);

		}
		else
			Eb = P1546_5(f,t,p,h1,d_km,tetamax15,&E0fs);
	}

	*Ebfs=E0fs;
	return Eb;
}

double CP1546_Functions::P1546_5(double f,double t,int p,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;
	double finf, fsup , Einf,Efsinf,Esup,Efssup;

	if (f<600.0)		{	finf=100.0; fsup=600.0;		}
	else if (f>600.0)	{	finf=600.0; fsup=2000.0;	}
 
	Einf = P1546_4(finf,t,p,h1,d_km,tetamax15,&Efsinf);
	Esup = P1546_4(fsup,t,p,h1,d_km,tetamax15,&Efssup);
	Eb = Einf + (Esup-Einf)*log10(f/finf)/log10(fsup/finf);
	E0fs = Efsinf + (Efssup-Efsinf)*log10(f/finf)/log10(fsup/finf); 

	*Ebfs=E0fs;
	return Eb;
}

double CP1546_Functions::P1546_4(double f,double t,int p,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;
	t=min(50.0,max(1.0,t));

	if ((t==1.0) || (t==10.0) || (t==50.0) ) 	Eb = P1546_3(f,t,p,h1,d_km,tetamax15,&E0fs);
	else
	{
		double tinf, tsup , Qt,Qinf,Qsup,Einf,Efsinf,Esup,Efssup;

		if ( (t>1.0) && (t<10.0) )			{	tinf=1.0; tsup=10.0;	}
		else if ( (t>10.0) && (t<50.0) )	{	tinf=10.0; tsup=50.0;	}

		Qt = Qi(t);
		Qinf = Qi(tinf);
		Qsup = Qi(tsup);
		Einf = P1546_3(f,tinf,p,h1,d_km,tetamax15,&Efsinf);
		Esup = P1546_3(f,tsup,p,h1,d_km,tetamax15,&Efssup);

		Eb = (Esup*(Qinf-Qt)/(Qinf-Qsup)) + (Einf*(Qt-Qsup)/(Qinf-Qsup));
		E0fs = (Efssup*(Qinf-Qt)/(Qinf-Qsup)) + (Efsinf*(Qt-Qsup)/(Qinf-Qsup));
	}
	*Ebfs=E0fs;
	return Eb;
}

double CP1546_Functions::Qi(double x)
{
	x=min(100.0-0.0000001,max(0.0000001,x));
	x=x/100.0;

	double  c0=2.515517,		c1=0.802853,		c2=0.010328,
			d1=1.432788,		d2=0.189269,		d3=0.001308;
	double T,P1,P2,P,Qi;

	if ( x<=0.5 )
	{
		T=sqrt(-2*log(x));
		P1=((c2*T+c1)*T)+c0;
		P2=((d3*T+d2)*T+d1)*T+1;
		P=P1/P2;
		Qi=T-P;
	}
	else
	{
		T=sqrt(-2*log(1-x));
		P1=((c2*T+c1)*T)+c0;
		P2=((d3*T+d2)*T+d1)*T+1;
		P=P1/P2;
		Qi=-(T-P);
	}
	return Qi;

}

double CP1546_Functions::P1546_3(double f,double t,int p,double h1,double d_km,double tetamax15,double *Ebfs) 
{
	double E0fs,Eb;

//	Eb = P1546_0(f,t,p,10.0,d_km,&E0fs);
//	*Ebfs=E0fs;

	if (p==1)
	{
		if (h1<0.0)		Eb = P1546_2(f,t,p,0.0,d_km,&E0fs) + TCAcorr1546(tetamax15,f);
		else			Eb = P1546_2(f,t,p,h1,d_km,&E0fs);
	}
	else 
	{
		if (h1<1.0)		Eb = P1546_2(f,t,p,1.0,d_km,&E0fs);
		else			Eb = P1546_2(f,t,p,h1,d_km,&E0fs);
	} 
	*Ebfs = E0fs;
	return Eb;
}

double CP1546_Functions::TCAcorr1546(double Tdeg,double f_MHz) 
{
	Tdeg=max(Tdeg , -0.8);
	Tdeg=min(Tdeg , 40.0);

	double v = 0.065 * Tdeg * sqrt(f_MHz);
	double v01 = v - 0.1;
	double Jv = 6.9 + 20.0*log10(sqrt(1.0 + v01*v01) + v01);
	double vp = 0.036 * sqrt(f_MHz);
	double vp01 = vp - 0.1;
	double Jvp = 6.9 + 20.0*log10(sqrt(1.0 + vp01*vp01) + vp01);
	double Corr = Jvp - Jv;

	return Corr;
}



double CP1546_Functions::P1546_2(double f,double t,int p,double h1,double d_km,double *Ebfs) 
{
	double E0fs,Eb,zzz1;
	zzz1 = P1546_0(f,t,p,10.0,d_km,&E0fs);
	*Ebfs=E0fs;

	if ( (h1>=10.0) && (h1<=1200.0) )
		Eb = P1546_1(f,t,p,h1,d_km,&zzz1);
	else if (h1>1200.0)
		Eb = P1546_0(f,t,p,600.0,d_km,&zzz1)+(P1546_0(f,t,p,1200.0,d_km,&zzz1)-P1546_0(f,t,p,600.0,d_km,&zzz1))*log10(h1/600.0)/log10(2.0);
	else if ( (h1>=0.0) && (h1<10.0) )
	{
		if (p==1)	Eb = P1546_1Land(f,t,p,h1,d_km);
		else		Eb = P1546_1Sea (f,t,p,h1,d_km);
	}
	Eb=min(Eb,E0fs);
	return Eb;
}


double CP1546_Functions::P1546_1Sea(double f,double t,int p,double h1,double d_km) 
{
	h1=max(1.0,h1);
	double E0fs,Eb;

	double Dh1=zD06(f,h1,10.0);
	double D20=zD06(f,20.0,10.0);

	if (d_km<=Dh1)
		E0fs = P1546_0(f,t,p,10.0,d_km,&Eb);
	else if ( (d_km>Dh1) && (d_km<D20) )
	{	
		double EDh1;
		E0fs = P1546_0(f,t,p,10.0,Dh1,&EDh1);
		double ED20 = P1546_0(f,t,p,10.0,D20,&E0fs) + (P1546_0(f,t,p,20.0,D20,&E0fs) - P1546_0(f,t,p,10.0,D20,&E0fs))*log10(h1/10.0)/log10(2.0);
		Eb = EDh1 + (ED20 - EDh1) * log10(d_km/Dh1) / log10(D20/Dh1);
	}
	else if (d_km>=D20)
	{
		double Ep = P1546_0(f,t,p,10.0,d_km,&E0fs)+(P1546_0(f,t,p,20.0,d_km,&E0fs) - P1546_0(f,t,p,10.0,d_km,&E0fs)) * log10(h1/10.0) / log10(2.0);
		double Epp = P1546_1Land(f,t,p,h1,d_km);
		double Fs = (d_km - D20) / d_km;
		Eb = Ep * (1.0-Fs) + Epp * Fs;
	}
	return Eb;

}

double CP1546_Functions::zD06(double f,double h1,double h2) 
{
	h1=max(0.0,h1);
	double Df=0.0000389*f*h1*h2;
	double Dh=4.1*(sqrt(h1)+sqrt(h2));
	double y=Df*Dh/(Df+Dh);
	y=max(0.001,y);
	return y;
}

double CP1546_Functions::P1546_1Land(double f,double t,int p,double h1,double d_km) 
{
	double ds=4.1*sqrt(h1);
	double d0=4.1*sqrt(10.0);
	double E0fs,Eb;

	if (d_km<ds)
		Eb = P1546_0(f,t,p,10.0,d0,&E0fs) + P1546_0(f,t,p,10.0,d_km,&E0fs) - P1546_0(f,t,p,10.0,ds,&E0fs);
	else
		Eb = P1546_0(f,t,p,10.0, (d_km + d0 - ds),&E0fs);

	return Eb;
}

double CP1546_Functions::P1546_1(double f,double t,int p,double h1,double d_km,double *Ebfs) 
{
	double Eb,E0fs;

	if ( (h1==10.0) ||(h1==20.0) ||(h1==37.5) || (h1==75.0) || (h1==150.0) || (h1==300.0) || (h1==600.0) || (h1==1200.0) )
		Eb= P1546_0( f , t , p , h1 , d_km , &E0fs );
	else
	{
		double h11,h12,E1,E2;
		
		if		( (h1 > 10.0) && (h1 < 20.0  ) )	{h11=10.0;h12=20.0;  }
		else if	( (h1 > 20.0) && (h1 < 37.5  ) )	{h11=20.0;h12=37.5;  }
		else if	( (h1 > 37.5) && (h1 < 75.0  ) )	{h11=37.5;h12=75.0;  }
		else if ( (h1 > 75.0  ) && (h1 < 150.0 ) )	{h11=75.0  ;h12=150.0; }
		else if ( (h1 > 150.0 ) && (h1 < 300.0 ) )	{h11=150.0 ;h12=300.0; }
		else if ( (h1 > 300.0 ) && (h1 < 600.0 ) )	{h11=300.0 ;h12=600.0; }
		else if ( (h1 > 600.0 ) && (h1 < 1200.0) )	{h11=600.0 ;h12=1200.0;}
		E1=P1546_0( f , t , p , h11 , d_km , &E0fs );
		E2=P1546_0( f , t , p , h12 , d_km , &E0fs );
		Eb=E1+(E2-E1)*log10(h1/h11)/log10(h12/h11);
	}
	*Ebfs=E0fs;
	return Eb;
}


