double CP1546_Functions::Interp1I(double *E0,double *D0,double E,int num) 
{
	double d1,d2,E1,E2,Db;
	if (E>E0[0])
	{
		d1=D0[0];d2=D0[1];
		E1=E0[0];E2=E0[1];
	}
	else if (E<E0[num-1])
	{
		d1=D0[num-2];d2=D0[num-1];
		E1=E0[num-2];E2=E0[num-1];
	}
	else
	{
		/////////////////////////////////////////////
		int n1=0;
		while(E<=E0[n1])
		{
			n1++;
			if (n1==num)
				break;
		}
		if (n1==0)
			n1=1;
		d1=D0[n1-1];E1=E0[n1-1];
		///////////////////////////////////////////////
		int n2=num-1;
		while(E>=E0[n2])
		{
			n2--;
			if (n2==0)
				break;
		}
		d2=D0[n2+1];E2=E0[n2+1];
		////////////////////////////////////////////////
	}
	Db=((E1==E2) ? d1 : d1*pow(10.0,(log10(d2/d1))*(E-E1)/(E2-E1)) );
	return Db;
}

